sm" : temp = get_profiles ( dsm = dsm , transect_file = transect_file , tr_ids = tr_ids , transect_index = i , step = sampling_step , location = location , date_string = date_string , add_xy = add_xy , add_terrain = slope , ) elif mode == "ortho" : temp = get_profile_dn ( ortho = dsm , transect_file = transect_file , transect_index = i , step = sampling_step , location = location , tr_ids = tr_ids , date_string = date_string , add_xy = add_xy , ) gdf = pd . concat ([ temp , gdf ], ignore_index = True ) counter += 1 if counter == len ( list_files ): print ( "Extraction successful" ) else : print ( f "There is something wrong with this dataset: { list_files [ counter ] } " ) end = time . time () timepassed = end - start print ( f "Number of points extracted: { gdf . shape [ 0 ] } 
